contracts/interfaces/IAddressProvider.so.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/// @title The interface for contracts to interact with the Blockswan Address Provider contract
interface IAddressProvider {
    /**
     * @dev Emitted when a new marketplaceId is set.
     * @param oldMarketplaceId The address of the old marketplaceId
     * @param newMarketplaceId The address of the new marketplaceId
     */
    event MarketplaceIdSet(
        string indexed oldMarketplaceId,
        string indexed newMarketplaceId
    );

    /**
     * @dev Emitted when a new contract is added to the AddressProvider.
     * @param _name The name assigned in the AddressProvider.
     * @param _address The address of the new contract
     */
    event ContractAdded(bytes32 _name, address _address);

    /**
     * @dev Emitted when a new contract is removed from the AddressProvider.
     * @param _name The contract name removed in the AddressProvider.
     * @param _address The address of the removed contract
     */
    event ContractRemoved(bytes32 _name, address _address);

    /**
     * @dev Emitted when a contract is upgraded in the AddressProvider.
     * @param _name The contract name upraded in the AddressProvider.
     * @param _oldAddress The old address prior upgrade
     * @param _newAddress The new address after upgrade
     */
    event ContractUpgraded(
        bytes32 _name,
        address _oldAddress,
        address _newAddress
    );

    /**
     * @notice returns contract address registered under given provider key
     * @param _name - registry key for lookup
     * @return contractAddr - address of contract registered under given provider key
     */
    function getContract(bytes32 _name) external view returns (address);

    /** @notice overloaded getContract to return explicit version of contract */
    function getContract(
        bytes32 _name,
        uint _version
    ) external view returns (address);

    /** @notice requires returned address !== 0 */
    function fetchContract(bytes32 _name) external view returns (address);

    /** @notice requires returned address !== 0 */
    function fetchContract(
        bytes32 _name,
        uint _version
    ) external view returns (address);

    function getContractVersionCount(
        bytes32 _name
    ) external view returns (uint);

    /**
     * @dev addContract does two things:
     *      1.) registers the address of given ProviderContract in the registry
     *      2.) sets the registry address in given ProviderContract so only
     *          the registry can call functions on given contract
     */
    function addContract(bytes32 _name, address _address) external;

    /**
     * @dev removes contract address registered under given provider key
     * @param _name - registry key for lookup
     */
    function removeContract(bytes32 _name) external;

    /**
     * @notice replaces contract address registered under given key with provided address
     * @param _name - registry key for lookup
     * @param _newAddress - new contract address to register under given key
     */
    function upgradeContract(bytes32 _name, address _newAddress) external;
}

contracts/interfaces/IBSWAN.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "../imports/openzeppelin/contracts/IERC20.sol";

/**
 * @title IBSWAN
 * @author Blockswan
 * @notice Defines the basic interface for the Blockswan Trust
 **/
interface IBSWAN is IERC20 {
    /**
     * @notice Update the DAT config.
     * @param _newOwner The new owner of the DAT
     * @param _revenueCommitmentBasisPoints the share of revenues tranfered to the buyback reserve
     * @param _minInvestment the min investment required in currency value
     */
    function updateConfig(
        address payable _newOwner,
        uint256 _revenueCommitmentBasisPoints,
        uint256 _minInvestment
    ) external;

    /**
     * @notice Update the DAT config.
     * @param _currencyValue the quantity of currency to buy
     * @return tokenValue the quantity of BSWAN bought for the _currencyValue
     */
    function estimateBuyValue(
        uint256 _currencyValue
    ) external view returns (uint256);

    /** @notice Purchase BSWAN tokens with the given amount of currency.
     * @param _to The account to receive the BSWAN tokens from this purchase.
     * @param _currencyValue How much currency to spend in order to buy BSWAN.
     * @param _minTokensBought Buy at least this many BSWAN tokens or the transaction reverts.
     * @dev _minTokensBought is necessary as the price will change if some elses transaction mines after
     * yours was submitted.
     */
    function buy(
        address _to,
        uint256 _currencyValue,
        uint256 _minTokensBought
    ) external payable;

    /// Pay
    function estimatePayValue(
        uint256 _currencyValue
    ) external view returns (uint256);

    /**
     * @dev Pay the organization on-chain.
     * @param _to The account which receives tokens for the contribution. If this address
     * is not authorized to receive tokens then they will be sent to the beneficiary account instead.
     * @param _currencyValue How much currency which was paid.
     */
    function pay(address _to, uint _currencyValue) external payable;

    /// @notice The address of the token used as reserve in the bonding curve
    /// (e.g. the DAI contract)
    function currency() external returns (IERC20);
}

contracts/interfaces/IGig.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IAddressProvider} from "./IAddressProvider.sol";
import {DataTypes} from "../protocol/libraries/types/DataTypes.sol";
import {OutputTypes} from "../protocol/libraries/types/OutputTypes.sol";
import {InputTypes} from "../protocol/libraries/types/InputTypes.sol";

/**
 * @title IGig
 * @author Blockswan
 * @notice Defines the basic interface for a blockswan gig contract.
 **/
interface IGig {
    /**
     * @dev Emitted when a new gig is created.
     * @param gigId The id of the gig
     * @param userId The user creating the gig
     * @param metadata The gig metadata
     * @param packages The gig packages
     */
    event GigAdded(
        uint256 indexed gigId,
        uint256 indexed userId,
        string metadata,
        DataTypes.Package[3] packages
    );

    /**
     * @dev Emitted when a gig is edited.
     * @param gigId The id of the gig
     * @param userId The user creating the gig
     * @param metadata The gig metadata
     * @param packages The gig packages
     */
    event GigEdited(
        uint256 indexed gigId,
        uint256 indexed userId,
        string metadata,
        DataTypes.Package[3] packages
    );

    /**
     * @notice Returns the gig data.
     * @param gigId The id of the gig.
     * @return The data associated to the gigId
     **/
    function getGigById(
        uint256 gigId
    ) external view returns (OutputTypes.GigOutput memory);

    /**
     * @notice Returns the list of all initialized gigs.
     * @return A data array of the gigs.
     **/
    function getGigList()
        external
        view
        returns (OutputTypes.UserOutput[] memory);

    /**
     * @notice Returns the current amount of gigs
     * @return the current amount of gigs
     **/
    function getGigsCount() external view returns (uint256);

    /**
     * @notice Create a new gig with the msg.sender wallet
     * @param metadata the gig related information
     * @param packages the packages inside the gig
     **/
    function createGig(
        string memory metadata,
        DataTypes.Package[3] memory packages
    ) external;

    /**
     * @notice retrieves the inviters wallet for a given wallet account
     * @param account the wallet to get the inviters from
     * @return address the pubkey of inviter0
     * @return address the pubkey of inviter1
     **/
    function getInvitersByUserAddress(
        address account
    ) external view returns (address, address);

    /**
     * @notice retrieves the inviters wallet for a given userId
     * @param userId the user id to get the inviters from
     * @return address the pubkey of inviter0
     * @return address the pubkey of inviter1
     **/
    function getInvitersById(
        uint256 userId
    ) external view returns (address, address);

    /**
     * @notice add the order id to the gig orderIds set.
     * @param newOrderId the new orderId.
     * @param gigId the new gig id.
     * @param packageId the packages inside the gig
     * @return success the relation has been addded.
     * @return metadata the gig metadata
     * @return packages the gig packages
     **/
    function createOrder(
        uint256 newOrderId,
        uint256 gigId,
        uint256 packageId
    ) external returns (bool, string memory, DataTypes.Package memory);
}

contracts/interfaces/IProtocolConfigurat.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IAddressProvider} from "./IAddressProvider.sol";
import {IAccessControlEnumerable} from "../imports/openzeppelin/contracts/IAccessControlEnumerable.sol";
import {DataTypes} from "../protocol/libraries/types/DataTypes.sol";

/**
 * @title IProtocolConfigurator
 * @author Blockswan
 * @notice Defines the basic interface for the ProtocolConfigurator
 **/
interface IProtocolConfigurator {
    /**
     * @notice Retrieves the parameters when enterering the protocol as a buyer.
     * @return parameters The paramaters  when accessing the role status
     */
    function getBuyerEntryParams()
        external
        view
        returns (DataTypes.EntryParams memory);

    // extends the above function specifying a version to retrieve
    function getBuyerEntryParams(
        uint256 version
    ) external view returns (DataTypes.EntryParams memory);

    /**
     * @notice Update the  parameters when enterering the protocol as as buyer.
     * @param newParams The new buyer entry paramaters
     */
    function updateBuyerEntryParams(
        DataTypes.EntryParams memory newParams
    ) external;

    /**
     * @notice Retrieves the parameters when enterering the protocol as a seller.
     * @return parameters The paramaters  when accessing the role status
     */
    function getSellerEntryParams()
        external
        view
        returns (DataTypes.EntryParams memory);

    // extends the above function specifying a version to retrieve
    function getSellerEntryParams(
        uint256 version
    ) external view returns (DataTypes.EntryParams memory);

    /**
     * @notice Update the  parameters when enterering the protocol as as seller.
     * @param newParams The new buyer entry paramaters
     */
    function updateSellerEntryParams(
        DataTypes.EntryParams memory newParams
    ) external;

    /**
     * @notice Retrieves the retribution percentage values of the protocol.
     * @return retributions The retribution percentage values of the protocol.
     */
    function getRetributionParams()
        external
        view
        returns (DataTypes.RetributionParams memory);

    // extends the above function specifying a version to retrieve
    function getRetributionParams(
        uint256 version
    ) external view returns (DataTypes.RetributionParams memory);

    /**
     * @notice Update the retributin  parameters of the protocol.
     * @param newParams The new retribution paramaters
     */
    function updateRetributionParams(
        DataTypes.RetributionParams memory newParams
    ) external;

    /**
     * @notice Retrieves the parameters when creating a gig.
     * @return parameters The parameters when creating a gig
     */
    function getGigCreationParams()
        external
        view
        returns (DataTypes.CreationParams memory);

    // extends the above function specifying a version to retrieve
    function getGigCreationParams(
        uint256 version
    ) external view returns (DataTypes.CreationParams memory);

    /**
     * @notice Update the gig creation parameters of the protocol.
     * @param newParams The new gig creation paramaters
     */
    function updateGigCreationParams(
        DataTypes.CreationParams memory newParams
    ) external;

    /**
     * @notice Retrieves the parameters when creating an order.
     * @return parameters The parameters when creating an order.
     * @return sellerFeesParamsVersion The latest fee structur version that will be applied to the seller upon order completion.
     */
    function getOrderCreationParams()
        external
        view
        returns (DataTypes.OrderPriceParams memory, uint256);

    // extends the above function specifying a version to retrieve
    function getOrderCreationParams(
        uint256 version
    ) external view returns (DataTypes.OrderPriceParams memory, uint256);

    /**
     * @notice Update the order creation parameters of the protocol.
     * @param newParams The new order creation paramaters
     */
    function updateOrderCreationParams(
        DataTypes.OrderPriceParams memory newParams
    ) external;

    /**
     * @notice Retrieves the protocol delay timestamp.
     * @return parameters The delay timestamp parametes
     */
    function getDelayTimestamp()
        external
        view
        returns (DataTypes.DelayTimestamp memory);

    // extends the above function specifying a version to retrieve
    function getDelayTimestamp(
        uint256 version
    ) external view returns (DataTypes.DelayTimestamp memory);

    /**
     * @notice Update the delays timestamp parameters of the protocol.
     * @param newParams The new order creation paramaters
     */
    function updateDelayTimestamp(
        DataTypes.DelayTimestamp memory newParams
    ) external;

    // extends the above function specifying a version to retrieve
    function getSellerOrderFees(
        uint256 version
    ) external view returns (DataTypes.OrderPriceParams memory);
}

