// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {EnumerableSet} from "../../../imports/openzeppelin/contracts/EnumerableSet.sol";
import {IERC20} from "../../../imports/openzeppelin/contracts/IERC20.sol";
import {Counters} from "../../../imports/openzeppelin/contracts/Counters.sol";
import {InputTypes} from "../types/InputTypes.sol";
import {DataTypes} from "../types/DataTypes.sol";
import {OutputTypes} from "../types/OutputTypes.sol";
import {Errors} from "../helpers/Errors.sol";
import {OrderDataLogic} from "./OrderDataLogic.sol";
import {PercentageMath} from "../../../imports/aave/contracts/PercentageMath.sol";

/**
 * @title Order logic library
 * @author Blockswan
 * @notice Implements the logic for order specific functions
 */
library OrderLogic {
    using EnumerableSet for EnumerableSet.UintSet;
    using OrderDataLogic for DataTypes.Order;
    using Counters for Counters.Counter;
    using PercentageMath for uint256;

    function getOrderById(
        uint256 orderId,
        mapping(uint256 => DataTypes.Order) storage orders
    ) public view returns (DataTypes.Order storage) {
        return orders[orderId];
    }

    function executeCreateOrder(
        EnumerableSet.UintSet storage orderIds,
        mapping(uint256 => DataTypes.Order) storage orders,
        InputTypes.ExecuteCreateOrderInput memory params
    ) external returns (bool) {
        bool added = orderIds.add(params.relations.newId);
        DataTypes.Order storage order = getOrderById(
            params.relations.newId,
            orders
        );
        order.setMetadata(params.metadata);
        order.setBrief(params.brief);
        order.setCreatedAt();
        order.setBuyerId(params.relations.buyerId);
        order.setSellerId(params.relations.sellerId);
        order.setGigId(params.relations.gigId);
        order.setPackage(params.package);
        order.setToProceed(params.fees.toProceed);
        order.setToTrial(params.fees.toTrial);
        order.setSellerFeesVersion(params.fees.sellerFeesVersion);
        order.setCurrency(params.currency);
        return (added);
    }

    function executeConfirmOrder(
        uint256 orderId,
        uint256 sellerId,
        mapping(uint256 => DataTypes.Order) storage orders
    ) external {
        DataTypes.Order storage order = getOrderById(orderId, orders);
        require(isOrderSeller(sellerId, order), "not order seller");
        require(order.state == DataTypes.OrderState.UNCONFIRMED);
        order.setState(DataTypes.OrderState.CONFIRMED);
    }

    function executeAutoRefund(
        uint256 orderId,
        uint256 buyerId,
        uint256 selfRefundDelay,
        mapping(uint256 => DataTypes.Order) storage orders
    ) external returns (uint256, IERC20) {
        DataTypes.Order storage order = getOrderById(orderId, orders);
        require(isOrderBuyer(buyerId, order), "not order seller");
        require(order.state == DataTypes.OrderState.UNCONFIRMED);
        require(order.createdAt + selfRefundDelay >= block.timestamp);
        order.setState(DataTypes.OrderState.DONE);
        return (
            order.package.price + order.toTrial + order.toProceed,
            order.currency
        );
    }

    function executePayOrder(
        uint256 orderId,
        uint256 buyerId,
        mapping(uint256 => DataTypes.Order) storage orders
    )
        external
        returns (uint256, uint256, uint256, uint256, uint256, IERC20 currency)
    {
        DataTypes.Order storage order = getOrderById(orderId, orders);
        require(isOrderBuyer(buyerId, order), "not order buyer");
        require(order.state == DataTypes.OrderState.CONFIRMED);
        order.setState(DataTypes.OrderState.DONE);
        return (
            order.package.price,
            order.toTrial,
            order.toProceed,
            order.sellerId,
            order.sellerFeesVersion,
            order.currency
        );
    }

    function calcOrderPrice(
        DataTypes.OrderPriceParams memory params,
        uint256 orderPrice
    ) external pure returns (uint256, uint256, uint256) {
        uint256 toTrial = orderPrice.percentMul(params.trialPercent) +
            params.trialFlat;
        uint256 toProceed = orderPrice.percentMul(params.trialPercent) +
            params.trialFlat;
        return (toTrial, toProceed, orderPrice + toTrial + toProceed);
    }

    function format(
        DataTypes.Order storage order,
        uint256 id
    ) external view returns (OutputTypes.OrderOutput memory) {
        return (
            OutputTypes.OrderOutput({
                metadata: order.metadata,
                brief: order.brief,
                sellerFeesVersion: order.sellerFeesVersion,
                toTrial: order.toTrial,
                toProceed: order.toProceed,
                orderId: id,
                createdAt: order.createdAt,
                buyerId: order.buyerId,
                sellerId: order.sellerId,
                gigId: order.gigId,
                reviewIds: order.reviewIds.values(),
                package: order.package,
                state: order.state,
                currency: order.currency
            })
        );
    }

    function isOrderBuyer(
        uint256 userId,
        DataTypes.Order storage order
    ) public view returns (bool) {
        return (order.buyerId == userId);
    }

    function isOrderSeller(
        uint256 userId,
        DataTypes.Order storage order
    ) public view returns (bool) {
        return (order.sellerId == userId);
    }
}

contracts/protocol/libraries/logics/Para.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {EnumerableSet} from "../../../imports/openzeppelin/contracts/EnumerableSet.sol";
import {Errors} from "../helpers/Errors.sol";
import {InputTypes} from "../types/InputTypes.sol";
import {DataTypes} from "../types/DataTypes.sol";

/**
 * @title  Params logic library
 * @author Blockswan
 * @notice Implements the logic for protocol params functions
 */
library ParamsLogic {
    using EnumerableSet for EnumerableSet.UintSet;

    function getEntryParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.EntryParams) storage history
    ) external view returns (DataTypes.EntryParams memory) {
        return history[params.at(params.length() - 1)];
    }

    function getEntryParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.EntryParams) storage history,
        uint256 version
    ) external view returns (DataTypes.EntryParams memory) {
        return history[params.at(version)];
    }

    function updateEntryParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.EntryParams) storage history,
        DataTypes.EntryParams memory newParams
    ) external {
        uint256 newParamId = params.length();
        params.add(newParamId);
        history[newParamId] = newParams;
    }

    function getRetributionParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.RetributionParams) storage history
    ) external view returns (DataTypes.RetributionParams memory) {
        return history[params.at(params.length() - 1)];
    }

    function getRetributionParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.RetributionParams) storage history,
        uint256 version
    ) external view returns (DataTypes.RetributionParams memory) {
        return history[params.at(version)];
    }

    function updateRetributionParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.RetributionParams) storage history,
        DataTypes.RetributionParams memory newParams
    ) external {
        uint256 newParamId = params.length();
        params.add(newParamId);
        history[newParamId] = newParams;
    }

    function getCreationParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.CreationParams) storage history
    ) external view returns (DataTypes.CreationParams memory) {
        return history[params.at(params.length() - 1)];
    }

    function getCreationParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.CreationParams) storage history,
        uint256 version
    ) external view returns (DataTypes.CreationParams memory) {
        return history[params.at(version)];
    }

    function updateCreationParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.CreationParams) storage history,
        DataTypes.CreationParams memory newParams
    ) external {
        uint256 newParamId = params.length();
        params.add(newParamId);
        history[newParamId] = newParams;
    }

    function getPriceParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.OrderPriceParams) storage history
    ) external view returns (DataTypes.OrderPriceParams memory) {
        return history[params.at(params.length() - 1)];
    }

    function getPriceParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.OrderPriceParams) storage history,
        uint256 version
    ) external view returns (DataTypes.OrderPriceParams memory) {
        return history[params.at(version)];
    }

    function updatePriceParams(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.OrderPriceParams) storage history,
        DataTypes.OrderPriceParams memory newParams
    ) external {
        uint256 newParamId = params.length();
        params.add(newParamId);
        history[newParamId] = newParams;
    }

    function getLatestVersion(
        EnumerableSet.UintSet storage versions
    ) external view returns (uint256) {
        return versions.length() - 1;
    }

    function getDelayTimestamp(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.DelayTimestamp) storage history
    ) external view returns (DataTypes.DelayTimestamp memory) {
        return history[params.at(params.length() - 1)];
    }

    function getDelayTimestamp(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.DelayTimestamp) storage history,
        uint256 version
    ) external view returns (DataTypes.DelayTimestamp memory) {
        return history[params.at(version)];
    }

    function updateDelayTimestamp(
        EnumerableSet.UintSet storage params,
        mapping(uint256 => DataTypes.DelayTimestamp) storage history,
        DataTypes.DelayTimestamp memory newParams
    ) external {
        uint256 newParamId = params.length();
        params.add(newParamId);
        history[newParamId] = newParams;
    }
}

