// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {AccessControlEnumerable} from "../../imports/openzeppelin/contracts/AccessControlEnumerable.sol";
import {Strings} from "../../imports/openzeppelin/contracts/Strings.sol";
import {IAddressProvider} from "../../interfaces/IAddressProvider.sol";
import {IACLManager} from "../../interfaces/IACLManager.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {RegistryKeys} from "../libraries/helpers/RegistryKeys.sol";
import {RoleKeys} from "../libraries/helpers/RoleKeys.sol";
import {ProviderContract} from "./ProviderContract.sol";

/**
 * @title ACLManager
 * @author Blockswan
 * @notice Access Control List Manager. Main registry of system roles and permissions.
 */
contract ACLManager is AccessControlEnumerable, IACLManager, ProviderContract {
    /**
     * @dev Constructor
     * @dev The ACL_ADMIN should be initialized at the addresses provider beforehand
     * @param provider The address of the AddressProvider
     */

    constructor(IAddressProvider provider) ProviderContract(provider) {
        address aclAdmin = ADDRESS_PROVIDER.fetchContract(
            RegistryKeys.ACL_ADMIN
        );
        require(aclAdmin != address(0), Errors.ZERO_ADDRESS_IS_INVALID);
        _setupRole(DEFAULT_ADMIN_ROLE, aclAdmin);
    }

    /// @inheritdoc IACLManager
    function setRoleAdmin(
        bytes32 role,
        bytes32 adminRole
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        _setRoleAdmin(role, adminRole);
    }
}

contracts/protocol/configuration/Address.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {Ownable} from "../../imports/openzeppelin/contracts/Ownable.sol";
import {IProviderContract} from "../../interfaces/IProviderContract.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {IAddressProvider} from "../../interfaces/IAddressProvider.sol";
import {Address} from "../../imports/openzeppelin/contracts/Address.sol";

/**
 * @title Central hub for ProvidersContracts that stores all contract addresses
 *    and facilitates inter-contract communication
 * @dev The provider acts as the central hub for all ProviderContracts.
 *    It stores the addresses for all contracts so when one contract wants to
 *    communicate with with another, it must go:
 *      Calling Contract -> AddressProvider -> Destination Contract
 *    It also serves as a communication hub and a version manager that can
 *    upgrade existing contracts and remove contracts
 * @dev for simplicity, this is the only contract that does not have de-coupled storage
 */

contract AddressProvider is Ownable, IAddressProvider {
    using Address for address;
    // Identifier of the Blockswan MarketPlace
    string private _marketplaceId;

    /**
     * @dev addressStorage mapping allows efficient lookup of current contract version
     *     	addressStorageHistory maintains record of all contract versions
     */
    mapping(bytes32 => address) private addressStorage;
    mapping(bytes32 => address[]) private addressStorageHistory;

    /**
     * @dev Constructor.
     * @param marketplaceId The identifier of the marketplace.
     * @param owner The owner address of this contract.
     */
    constructor(string memory marketplaceId, address owner) {
        _setMarketplaceId(marketplaceId);
        transferOwnership(owner);
    }

    function getMarketplaceId() external view returns (string memory) {
        return _marketplaceId;
    }

    function setMarketplaceId(
        string memory newMarketplaceId
    ) external onlyOwner {
        _setMarketplaceId(newMarketplaceId);
    }

    /// @inheritdoc IAddressProvider
    function addContract(
        bytes32 _name,
        address _address
    ) external override onlyOwner {
        require(
            addressStorage[_name] == address(0x00),
            Errors.CONTRACT_NAME_ALREADY_USED
        );
        require(_address != address(0x00), Errors.ZERO_ADDRESS_IS_INVALID);
        if (_address.isContract() && _name != bytes32("DAT")) {
            IProviderContract(_address).setProvider(this);
        }
        setAddress(_name, _address);
        emit ContractAdded(_name, _address);
    }

    /// @inheritdoc IAddressProvider
    function getContract(
        bytes32 _name
    ) public view override returns (address contractAddr) {
        return addressStorage[_name];
    }

    /// @inheritdoc IAddressProvider
    function getContract(
        bytes32 _name,
        uint _version
    ) public view override returns (address contractAddr) {
        // array length for key implies version number
        require(
            _version <= addressStorageHistory[_name].length,
            Errors.INDEX_OUT_OF_RANGE
        );
        return addressStorageHistory[_name][_version - 1];
    }

    /// @inheritdoc IAddressProvider
    function fetchContract(
        bytes32 _name
    ) external view override returns (address contractAddr) {
        address fetched = getContract(_name);
        require(fetched != address(0), Errors.ZERO_ADDRESS_IS_INVALID);
        return fetched;
    }

    /// @inheritdoc IAddressProvider
    function fetchContract(
        bytes32 _name,
        uint _version
    ) external view override returns (address contractAddr) {
        address fetched = getContract(_name, _version);
        require(fetched != address(0), Errors.ZERO_ADDRESS_IS_INVALID);
        return fetched;
    }

    /// @inheritdoc IAddressProvider
    function getContractVersionCount(
        bytes32 _name
    ) external view override returns (uint) {
        return addressStorageHistory[_name].length;
    }

    /// @inheritdoc IAddressProvider
    function removeContract(bytes32 _name) external override onlyOwner {
        address contractAddress = addressStorage[_name];
        require(
            contractAddress != address(0x00),
            Errors.ZERO_ADDRESS_IS_INVALID
        );
        IProviderContract(contractAddress).kill();
        setAddress(_name, address(0x00));
        emit ContractRemoved(_name, contractAddress);
    }

    /// @inheritdoc IAddressProvider
    function upgradeContract(
        bytes32 _name,
        address _newAddress
    ) external override onlyOwner {
        address oldAddress = addressStorage[_name];
        require(oldAddress != address(0x00), Errors.ZERO_ADDRESS_IS_INVALID);
        IProviderContract(oldAddress).kill();
        IProviderContract(_newAddress).setProvider(this);
        setAddress(_name, _newAddress);
        emit ContractUpgraded(_name, oldAddress, _newAddress);
    }

    /**
     * @param _key the key for the contract address
     * @param _value the contract address
     */
    function setAddress(bytes32 _key, address _value) private {
        // main map for cheap lookup
        addressStorage[_key] = _value;
        // keep track of contract address history
        addressStorageHistory[_key].push(_value);
    }

    /**
     * @notice Updates the identifier of the Blockswan marketplace.
     * @param newMarketplaceId The new id of the marketplace
     **/
    function _setMarketplaceId(string memory newMarketplaceId) internal {
        string memory oldMarketplaceId = _marketplaceId;
        _marketplaceId = newMarketplaceId;
        emit MarketplaceIdSet(oldMarketplaceId, newMarketplaceId);
    }
}

contracts/protocol/configuration/Protoco.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {Ownable} from "../../imports/openzeppelin/contracts/Ownable.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {EnumerableSet} from "../../imports/openzeppelin/contracts/EnumerableSet.sol";
import {DataTypes} from "../libraries/types/DataTypes.sol";
import {RegistryKeys} from "../libraries/helpers/RegistryKeys.sol";
import {RoleKeys} from "../libraries/helpers/RoleKeys.sol";
import {IProviderRegistry} from "../../interfaces/IProviderRegistry.sol";
import {IProtocolConfigurator} from "../../interfaces/IProtocolConfigurator.sol";
import {IAddressProvider} from "../../interfaces/IAddressProvider.sol";

import {ProviderContract} from "./ProviderContract.sol";
import {ParamsLogic} from "../libraries/logics/ParamsLogic.sol";

/**
 * @title Protocol Configurator
 * @author Blockswan
 * @notice Contains all the protocol configuration
 **/

contract ProtocolConfigurator is ProviderContract {
    using EnumerableSet for EnumerableSet.UintSet;
    using ParamsLogic for EnumerableSet.UintSet;

    EnumerableSet.UintSet private _buyerEntryParamsVersions;
    mapping(uint256 => DataTypes.EntryParams) private _buyerEntryParamsHistory;

    EnumerableSet.UintSet private _sellerEntryParamsVersions;
    mapping(uint256 => DataTypes.EntryParams) private _sellerEntryParamsHistory;

    EnumerableSet.UintSet private _retributionParamsVersions;
    mapping(uint256 => DataTypes.RetributionParams)
        private _retributionParamsHistory;

    EnumerableSet.UintSet private _gigCreationParamsVersions;
    mapping(uint256 => DataTypes.CreationParams)
        private _gigCreationParamsHistory;

    EnumerableSet.UintSet private _orderCreationParamsVersions;
    mapping(uint256 => DataTypes.FeeParams) private _orderCreationParamsHistory;

    EnumerableSet.UintSet private _sellerOrderFeesParamsVersions;
    mapping(uint256 => DataTypes.FeeParams)
        private _sellerOrderFeesParamsHistory;

    EnumerableSet.UintSet private _delayTimestampVersions;
    mapping(uint256 => DataTypes.DelayTimestamp) private _delayTimestampHistory;

    EnumerableSet.UintSet private _disputeParamsVersions;
    mapping(uint256 => DataTypes.DisputeParams) private _disputeParamsHistory;

    constructor(IAddressProvider provider) ProviderContract(provider) {}

    function getBuyerEntryParams()
        external
        view
        returns (DataTypes.EntryParams memory)
    {
        return
            _buyerEntryParamsVersions.getEntryParams(_buyerEntryParamsHistory);
    }

    function getBuyerEntryParams(
        uint256 version
    ) external view returns (DataTypes.EntryParams memory) {
        return
            _buyerEntryParamsVersions.getEntryParams(
                _buyerEntryParamsHistory,
                version
            );
    }

    function updateBuyerEntryParams(
        DataTypes.EntryParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _buyerEntryParamsVersions.updateEntryParams(
            _buyerEntryParamsHistory,
            newParams
        );
    }

    function getSellerEntryParams()
        external
        view
        returns (DataTypes.EntryParams memory)
    {
        return
            _sellerEntryParamsVersions.getEntryParams(
                _sellerEntryParamsHistory
            );
    }

    function getSellerEntryParams(
        uint256 version
    ) external view returns (DataTypes.EntryParams memory) {
        return
            _sellerEntryParamsVersions.getEntryParams(
                _sellerEntryParamsHistory,
                version
            );
    }

    function updateSellerEntryParams(
        DataTypes.EntryParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _sellerEntryParamsVersions.updateEntryParams(
            _sellerEntryParamsHistory,
            newParams
        );
    }

    function getRetributionParams()
        external
        view
        returns (DataTypes.RetributionParams memory)
    {
        return
            _retributionParamsVersions.getRetributionParams(
                _retributionParamsHistory
            );
    }

    function getRetributionParams(
        uint256 version
    ) external view returns (DataTypes.RetributionParams memory) {
        return
            _retributionParamsVersions.getRetributionParams(
                _retributionParamsHistory,
                version
            );
    }

    function updateRetributionParams(
        DataTypes.RetributionParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _retributionParamsVersions.updateRetributionParams(
            _retributionParamsHistory,
            newParams
        );
    }

    function getGigCreationParams()
        external
        view
        returns (DataTypes.CreationParams memory)
    {
        return
            _gigCreationParamsVersions.getCreationParams(
                _gigCreationParamsHistory
            );
    }

    function getGigCreationParams(
        uint256 version
    ) external view returns (DataTypes.CreationParams memory) {
        return
            _gigCreationParamsVersions.getCreationParams(
                _gigCreationParamsHistory,
                version
            );
    }

    function updateGigCreationParams(
        DataTypes.CreationParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _gigCreationParamsVersions.updateCreationParams(
            _gigCreationParamsHistory,
            newParams
        );
    }

    function getOrderCreationParams()
        external
        view
        returns (DataTypes.FeeParams memory)
    {
        return (
            _orderCreationParamsVersions.getFeeParams(
                _orderCreationParamsHistory
            )
        );
    }

    function getOrderCreationParams(
        uint256 version
    ) external view returns (DataTypes.FeeParams memory) {
        return (
            _orderCreationParamsVersions.getFeeParams(
                _orderCreationParamsHistory,
                version
            )
        );
    }

    function updateOrderCreationParams(
        DataTypes.FeeParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _orderCreationParamsVersions.updateFeeParams(
            _orderCreationParamsHistory,
            newParams
        );
    }

    function getSellerOrderFees()
        external
        view
        returns (DataTypes.FeeParams memory)
    {
        return
            _sellerOrderFeesParamsVersions.getFeeParams(
                _sellerOrderFeesParamsHistory
            );
    }

    function getSellerOrderFees(
        uint256 version
    ) external view returns (DataTypes.FeeParams memory) {
        return
            _sellerOrderFeesParamsVersions.getFeeParams(
                _sellerOrderFeesParamsHistory,
                version
            );
    }

    function updateSellerOrderFees(
        DataTypes.FeeParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _sellerOrderFeesParamsVersions.updateFeeParams(
            _sellerOrderFeesParamsHistory,
            newParams
        );
    }

    function getDelayTimestamp()
        external
        view
        returns (DataTypes.DelayTimestamp memory)
    {
        return
            _delayTimestampVersions.getDelayTimestamp(_delayTimestampHistory);
    }

    function getDelayTimestamp(
        uint256 version
    ) external view returns (DataTypes.DelayTimestamp memory) {
        return
            _delayTimestampVersions.getDelayTimestamp(
                _delayTimestampHistory,
                version
            );
    }

    function updateDelayTimestamp(
        DataTypes.DelayTimestamp memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _delayTimestampVersions.updateDelayTimestamp(
            _delayTimestampHistory,
            newParams
        );
    }

    function getDisputeParams()
        external
        view
        returns (DataTypes.DisputeParams memory)
    {
        return _disputeParamsVersions.getDisputeParams(_disputeParamsHistory);
    }

    function getDisputeParams(
        uint256 version
    ) external view returns (DataTypes.DisputeParams memory) {
        return
            _disputeParamsVersions.getDisputeParams(
                _disputeParamsHistory,
                version
            );
    }

    function updateDisputeParams(
        DataTypes.DisputeParams memory newParams
    ) external onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        _disputeParamsVersions.updateDisputeParams(
            _disputeParamsHistory,
            newParams
        );
    }
}

