//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";

import {Ownable} from "../../imports/openzeppelin/contracts/Ownable.sol";
import {IERC20} from "../../imports/openzeppelin/contracts/IERC20.sol";
import {GPv2SafeERC20} from "../../imports/gnosis/contracts/GPv2SafeERC20.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {IAddressProvider} from "../../interfaces/IAddressProvider.sol";
import {IProtocolConfigurator} from "../../interfaces/IProtocolConfigurator.sol";

import {IProviderContract} from "../../interfaces/IProviderContract.sol";
import {IACLManager} from "../../interfaces/IACLManager.sol";
import {IBSWAN} from "../../interfaces/IBSWAN.sol";
import {IXP} from "../../interfaces/IXP.sol";
import {IUser} from "../../interfaces/IUser.sol";
import {InputTypes} from "../libraries/types/InputTypes.sol";
import {DataTypes} from "../libraries/types/DataTypes.sol";
import {RegistryKeys} from "../libraries/helpers/RegistryKeys.sol";
import {RoleKeys} from "../libraries/helpers/RoleKeys.sol";

/**
 * @title Parent class to all contracts used to check that a contract is registerable
 * @notice ProviderContract is Ownable so the deployer can re-register it against new registries
 * @dev The Provider uses this to talk to all contracts that inherit from this contract.
 */

contract ProviderContract is Ownable, IProviderContract {
    using GPv2SafeERC20 for IERC20;
    IAddressProvider public ADDRESS_PROVIDER;

    uint256 public MAX_UINT = 2 ** 256 - 1;

    /// @notice all contracts that inherit from ProviderContract are automatically Ownable()
    /// @dev internal constructor makes ProviderContract abstract
    constructor(IAddressProvider provider) Ownable() {
        require(
            address(provider) != address(0x00),
            Errors.ZERO_ADDRESS_IS_INVALID
        );

        ADDRESS_PROVIDER = IAddressProvider(provider);
    }

    /// @notice only allow  contracts to be called by the respective agent.
    ///     i.e. GigStorage methods can only be invoked by GigFactory.
    modifier onlyProvider(bytes32 _name) {
        require(
            _msgSender() == fetchContract(_name),
            Errors.ONLY_PROVIDER_ALLOWED
        );
        _;
    }

    /// @notice only allow function contracts to be called by the respective role.
    ///     i.e. GigStorage methods can only be invoked by BUYER_ROLE.
    modifier onlyProtocolRole(bytes32 _role) {
        require(
            hasProtocolRole(_role, _msgSender()),
            "Requires msg.sender has role"
        );
        _;
    }

    modifier onlyStillBuyer() {
        require(isStillBuyer(_msgSender()), Errors.ONLY_BUYER);
        _;
    }

    modifier onlyStillSeller() {
        require(isStillSeller(_msgSender()), Errors.ONLY_SELLER);
        _;
    }

    function hasProtocolRole(
        bytes32 _role,
        address account
    ) public view returns (bool) {
        bool hasRole = IACLManager(fetchContract(RegistryKeys.ACL_MANAGER))
            .hasRole(_role, account);
        return hasRole;
    }

    // function hasProtocolRoles(
    //     bytes32[] memory _roles,
    //     address account
    // ) public view returns (bool) {
    //     for (uint256 i; i < _roles.length; i++) {
    //         bool hasRole = IACLManager(fetchContract(RegistryKeys.ACL_MANAGER))
    //             .hasRole(_roles[i], account);
    //         if (hasRole) {
    //             return true;
    //         }
    //     }
    //     return false;
    // }

    function isStillBuyer(address account) public view returns (bool) {
        return
            hasProtocolRole(RoleKeys.BUYER_ROLE, account) &&
            IUser(fetchContract(RegistryKeys.USER))
                .getUserByAddress(account)
                .buyerUntil >
            block.timestamp;
    }

    function isStillSeller(address account) public view returns (bool) {
        return
            hasProtocolRole(RoleKeys.SELLER_ROLE, account) &&
            IUser(fetchContract(RegistryKeys.USER))
                .getUserByAddress(account)
                .sellerUntil >
            block.timestamp;
    }

    /// @inheritdoc IProviderContract
    function setProvider(IAddressProvider _providerAddress) public override {
        require(
            address(_providerAddress) == address(0x00) ||
                address(ADDRESS_PROVIDER) == msg.sender ||
                this.owner() == msg.sender,
            "Can only be called if addressesProvider is empty, msg.sender or owner"
        );
        ADDRESS_PROVIDER = _providerAddress;
    }

    function fetchContract(
        bytes32 _name
    ) public view override returns (address) {
        return address(IAddressProvider(ADDRESS_PROVIDER).fetchContract(_name));
    }

    /// @inheritdoc IProviderContract
    function kill() external override {
        assert(msg.sender == address(ADDRESS_PROVIDER));
        selfdestruct(payable(address(ADDRESS_PROVIDER)));
    }

    /// @inheritdoc IProviderContract
    function ADDRESSES_PROVIDER()
        external
        view
        override
        returns (IAddressProvider)
    {
        return ADDRESS_PROVIDER;
    }

    function grantProtocolRole(bytes32 _role, address account) internal {
        IACLManager(fetchContract(RegistryKeys.ACL_MANAGER)).grantRole(
            _role,
            account
        );
    }

    function approve(
        address erc20
    ) public onlyProtocolRole(RoleKeys.PROTOCOL_ADMIN_ROLE) {
        address dat = fetchContract(RegistryKeys.DAT);
        IERC20(erc20).approve(dat, MAX_UINT);
    }

    function _pay(
        InputTypes.ProcessPaymentInput memory params,
        IBSWAN dat
    ) internal {
        dat.pay(params.inviter0, params.inviter0Rewards);
        dat.pay(params.inviter1, params.inviter1Rewards);
        dat.pay(address(0), params.remainingRewards);
    }

    function _processPayment(
        InputTypes.ProcessPaymentInput memory params
    ) internal {
        IBSWAN dat = IBSWAN(fetchContract(RegistryKeys.DAT));
        uint256 total = params.inviter0Rewards +
            params.inviter1Rewards +
            params.remainingRewards;
        IERC20(dat.currency()).safeTransferFrom(
            params.caller,
            address(this),
            total
        );
        _pay(params, dat);
    }

    function _giveXP(bytes32 _key, address _to) internal {
        IXP(fetchContract(RegistryKeys.XP)).mint(_key, _to);
    }

    function getProtocolRetributionParams()
        internal
        view
        returns (DataTypes.RetributionParams memory)
    {
        return
            IProtocolConfigurator(
                fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
            ).getRetributionParams();
    }

    function getProtocolDelayTimestamp()
        internal
        view
        returns (DataTypes.DelayTimestamp memory)
    {
        return
            IProtocolConfigurator(
                fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
            ).getDelayTimestamp();
    }

    function isGigOwner(
        uint256 userId,
        uint256 gigId,
        IUser UserContract
    ) public view returns (bool) {
        return UserContract.isGigOwner(userId, gigId);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {Ownable} from "../../imports/openzeppelin/contracts/Ownable.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {IProviderRegistry} from "../../interfaces/IProviderRegistry.sol";

/**
 * @title Provider Registry
 * @author Blockswan
 * @notice Main registry of Addresses_provider of Blockswan marketplaces.
 * @dev Used for indexing purposes of Blockswan protocol's marketplaces. The id assigned to an AddressesProvider refers to the
 * market it is connected with, for example with `1` for the Blockswan main market and `2` for the next created.
 **/

contract ProviderRegistry is Ownable, IProviderRegistry {
    // List of addresses providers
    address[] private _addressProvidersList;
    // Map of address provider ids (addressProvider => id)
    mapping(address => uint256) private _addressProviderToId;
    // Map of id to address provider (id => addressProvider)
    mapping(uint256 => address) private _idToAddressProvider;
    // Map of address provider list indexes (addressProvider => indexInList)
    mapping(address => uint256) private _addressProvidersIndexes;

    /**
     * @dev Constructor.
     * @param owner The owner address of this contract.
     */
    constructor(address owner) {
        transferOwnership(owner);
    }

    /// @inheritdoc IProviderRegistry
    function getAddressProvidersList()
        external
        view
        override
        returns (address[] memory)
    {
        return _addressProvidersList;
    }

    /// @inheritdoc IProviderRegistry
    function registerAddressProvider(
        address provider,
        uint256 id
    ) external override onlyOwner {
        require(id != 0, Errors.INVALID_ADDRESS_PROVIDER_ID);
        require(
            _idToAddressProvider[id] == address(0),
            Errors.INVALID_ADDRESS_PROVIDER_ID
        );
        require(
            _addressProviderToId[provider] == 0,
            Errors.ADDRESS_PROVIDER_ALREADY_ADDED
        );

        _addressProviderToId[provider] = id;
        _idToAddressProvider[id] = provider;

        _addToAddressProviderslist(provider);
        emit AddressProviderRegistered(provider, id);
    }

    /// @inheritdoc IProviderRegistry
    function unregisterAddressProvider(
        address provider
    ) external override onlyOwner {
        require(
            _addressProviderToId[provider] != 0,
            Errors.ADDRESS_PROVIDER_NOT_REGISTERED
        );
        uint256 old_id = _addressProviderToId[provider];
        _idToAddressProvider[old_id] = address(0);
        _addressProviderToId[provider] = 0;

        _removeFromAddressProvidersList(provider);

        emit AddressProviderUnregistered(provider, old_id);
    }

    /// @inheritdoc IProviderRegistry
    function getAddressProviderIdByAddress(
        address addresses_provider
    ) external view override returns (uint256) {
        return _addressProviderToId[addresses_provider];
    }

    /// @inheritdoc IProviderRegistry
    function getAddressProviderById(
        uint256 id
    ) external view override returns (address) {
        return _idToAddressProvider[id];
    }

    /**
     * @notice Adds the addresses provider address to the list.
     * @param provider The address of the protocol AddressesProvider
     */
    function _addToAddressProviderslist(address provider) internal {
        _addressProvidersIndexes[provider] = _addressProvidersList.length;
        _addressProvidersList.push(provider);
    }

    /**
     * @notice Removes the addresses provider address from the list.
     * @param provider The address of the AddressesProvider
     */
    function _removeFromAddressProvidersList(address provider) internal {
        uint256 index = _addressProvidersIndexes[provider];

        _addressProvidersIndexes[provider] = 0;

        // Swap the index of the last addresses provider in the list with the index of the provider to remove
        uint256 last_index = _addressProvidersList.length - 1;
        if (index < last_index) {
            address last_provider = _addressProvidersList[last_index];
            _addressProvidersList[index] = last_provider;
            _addressProvidersIndexes[last_provider] = index;
        }
        _addressProvidersList.pop();
    }
}

contracts/protocol/dispute/Dispute.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "hardhat/console.sol";
import {GPv2SafeERC20} from "../../imports/gnosis/contracts/GPv2SafeERC20.sol";
import {EnumerableSet} from "../../imports/openzeppelin/contracts/EnumerableSet.sol";
import {EnumerableMap} from "../../imports/openzeppelin/contracts/EnumerableMap.sol";
import {DataTypes} from "../libraries/types/DataTypes.sol";
import {OutputTypes} from "../libraries/types/OutputTypes.sol";
import {InputTypes} from "../libraries/types/InputTypes.sol";
import {IERC20} from "../../imports/openzeppelin/contracts/IERC20.sol";
import {DisputeLogic} from "../libraries/logics/DisputeLogic.sol";
import {InviterLogic} from "../libraries/logics/InviterLogic.sol";
import {Errors} from "../libraries/helpers/Errors.sol";

import {RegistryKeys} from "../libraries/helpers/RegistryKeys.sol";
import {XPKeys} from "../libraries/helpers/XPKeys.sol";
import {DisputeStorage} from "./DisputeStorage.sol";
import {ProviderContract} from "../configuration/ProviderContract.sol";
import {IProtocolConfigurator} from "../../interfaces/IProtocolConfigurator.sol";
import {IAddressProvider} from "../../interfaces/IAddressProvider.sol";
import {IUser} from "../../interfaces/IUser.sol";
import {IJury} from "../../interfaces/IJury.sol";
import {IBSWAN} from "../../interfaces/IBSWAN.sol";
import {IDispute} from "../../interfaces/IDispute.sol";
import {IGig} from "../../interfaces/IGig.sol";
import {IOrder} from "../../interfaces/IOrder.sol";
import {JuryLogic} from "../libraries/logics/JuryLogic.sol";

/**
 * @title	Dispute contract
 * @author	Blockswan
 * @notice  Dispute data within an Blockswan protocol's marketplace
 * - Users can:
 *   # raise a dispute via the Dispute contract
 *   # send evidence to a dispute
 *   # commit a vote on a dispute
 *   # vote on a dispute
 *   # appeal to a dispute resolution
 *  # execute a ruling on a dispute
 * @dev To be covered by a proxy contract, owned by the addressProvider of the specific marketplace
 * @dev All admin functions are callable by the protocolConfigurator contract defined also in the
 *   addressProvider
 **/

contract Dispute is DisputeStorage, ProviderContract {
    using EnumerableSet for EnumerableSet.UintSet;
    using DisputeLogic for DataTypes.Dispute;
    using GPv2SafeERC20 for IERC20;

    /**
     * @dev Constructor.
     * @param provider The address of the AddressProvider
     */
    constructor(IAddressProvider provider) ProviderContract(provider) {}

    function getDisputeCount() public view virtual returns (uint256) {
        uint256 count = _disputeIds.length();
        return count;
    }

    function getDisputeById(
        uint256 disputeId
    ) public view virtual returns (OutputTypes.DisputeOutput memory) {
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        return dispute.format(disputeId);
    }

    function getDisputeList()
        external
        view
        virtual
        returns (OutputTypes.DisputeOutput[] memory)
    {
        uint256 len = getDisputeCount();
        OutputTypes.DisputeOutput[]
            memory disputeList = new OutputTypes.DisputeOutput[](len);
        for (uint256 i = 0; i < len; i++) {
            disputeList[i] = getDisputeById(i);
        }
        return disputeList;
    }

    function createDispute(
        uint256 orderId,
        uint256 procecutorId,
        uint256 defendantId,
        address caller,
        DataTypes.Evidence memory evidence
    ) external onlyProvider(RegistryKeys.ORDER) returns (uint256) {
        uint256 newId = getDisputeCount();

        {
            IProtocolConfigurator protocolConfigurator = IProtocolConfigurator(
                fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
            );
            DataTypes.DisputeParams memory disputeParams = protocolConfigurator
                .getDisputeParams();

            _createDispute(
                newId,
                orderId,
                procecutorId,
                defendantId,
                caller,
                protocolConfigurator,
                disputeParams
            );
            sendEvidence(newId, caller, evidence);
        }
        return newId;
    }

    function _createDispute(
        uint256 newId,
        uint256 orderId,
        uint256 procecutorId,
        uint256 defendantId,
        address caller,
        IProtocolConfigurator protocolConfigurator,
        DataTypes.DisputeParams memory disputeParams
    ) internal {
        bool isCreated = DisputeLogic.executeCreateDispute(
            _disputeIds,
            _disputes,
            InputTypes.ExecuteCreateDisputeInput({
                newId: newId,
                orderId: orderId,
                procecutorId: procecutorId,
                defendantId: defendantId,
                maxVotes: disputeParams.maxVotes,
                totalFeesForJurors: _payDisputeContract(
                    disputeParams.feePerJuror * disputeParams.maxVotes,
                    caller
                ),
                evidenceUntil: getDelayTimestamp(protocolConfigurator).evidence,
                drawnJurors: drawJurors(disputeParams.maxVotes),
                tokensAtStakePerJuror: JuryLogic.calcTokenToFreeze(
                    disputeParams.minStake,
                    disputeParams.alpha
                )
            })
        );
        require(isCreated, Errors.DISPUTE_NOT_CREATED);
    }

    function getDelayTimestamp(
        IProtocolConfigurator protocolConfigurator
    ) public view returns (DataTypes.DelayTimestamp memory delayTimestamp) {
        delayTimestamp = protocolConfigurator.getDelayTimestamp();
        return delayTimestamp;
    }

    function sendEvidence(
        uint256 disputeId,
        address caller,
        DataTypes.Evidence memory evidence
    ) public {
        IUser UserContract = IUser(fetchContract(RegistryKeys.USER));
        require(
            isCallerUser(caller, evidence.userId, UserContract),
            Errors.CALLER_NOT_USER
        );
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        bool isSent = DisputeLogic.executeSendEvidence(dispute, evidence);
        require(isSent, Errors.EVIDENCE_NOT_SUBMITTED);
    }

    function passPhase(uint256 disputeId) public {
        DataTypes.DelayTimestamp memory delayTimestamps = IProtocolConfigurator(
            fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
        ).getDelayTimestamp();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        DataTypes.DisputeState state = dispute.state;
        require(
            state != DataTypes.DisputeState.EXECUTION,
            Errors.DS_IN_EXECUTION_PERIOD
        );
        if (state == DataTypes.DisputeState.EVIDENCE) {
            dispute.goCommitState(delayTimestamps);
        } else if (state == DataTypes.DisputeState.COMMIT) {
            dispute.goVoteState(delayTimestamps.vote);
        } else if (state == DataTypes.DisputeState.VOTE) {
            dispute.goAppealState(delayTimestamps.appeal);
        } else if (state == DataTypes.DisputeState.APPEAL) {
            dispute.goExecutionState();
        }
    }

    function commitVote(
        uint256 disputeId,
        uint256 choice,
        string memory salt
    ) public {
        address caller = _msgSender();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        dispute.executeCommitVote(choice, salt, caller);
    }

    function revealVote(
        uint256 disputeId,
        uint256 choice,
        string memory salt,
        string memory justification
    ) public {
        address caller = _msgSender();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        dispute.executeRevealVote(choice, salt, justification, caller);
    }

    function drawJurors(
        uint256 numberOfJurors
    ) internal returns (address[] memory jurorsAddresses) {
        IJury JuryContract = IJury(fetchContract(RegistryKeys.JURY));
        jurorsAddresses = JuryContract.drawJurors(numberOfJurors);
        JuryContract.freezeTokens(jurorsAddresses);
    }

    function appeal(uint256 disputeId) external {
        address caller = _msgSender();
        IUser UserContract = IUser(fetchContract(RegistryKeys.USER));
        uint256 callerId = UserContract.getIdByAddress(caller);
        DataTypes.DisputeParams memory disputeParams = IProtocolConfigurator(
            fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
        ).getDisputeParams();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        uint256 appealFeeInUSDC = dispute.calcAppealFees(
            disputeParams.feePerJuror
        );
        uint256 appealFeeRewardsinBSWAN = _payDisputeContract(
            appealFeeInUSDC,
            _msgSender()
        );
        dispute.executeAppeal(appealFeeRewardsinBSWAN, callerId);
    }

    function claimAsJudge(uint256 disputeId, uint256 roundId) external {
        address caller = _msgSender();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        OutputTypes.ExecuteClaimAsJudgeOutput memory claimParams = dispute
            .executeClaimAsJudge(caller, roundId);
        if (claimParams.isVoteCorrect) {
            _handleCorrectVoter(
                caller,
                claimParams.tokensAtStakePerJuror,
                claimParams.amountFromDisputeFees,
                claimParams.amountFromJurorsTokensAtStake
            );
        } else if (claimParams.isVoteInRange) {
            _handleInRangeVoter(caller, claimParams.tokensAtStakePerJuror);
        } else {
            require(false, Errors.VOTE_INCORRECT);
        }
    }

    function claimAppealFeeRewards(uint256 disputeId, uint256 roundId) public {
        address caller = _msgSender();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        uint256 callerId = IUser(fetchContract(RegistryKeys.USER))
            .getIdByAddress(caller);

        uint256 appealFeeAmount = dispute.executeClaimAppealFeeRewards(
            callerId,
            roundId
        );
        _transfer(
            appealFeeAmount,
            caller,
            address(IBSWAN(fetchContract(RegistryKeys.DAT)))
        );
    }

    function claimRuling(uint256 disputeId) external {
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        (
            uint256 ruling,
            uint256 orderId,
            uint256 procecutorId,
            uint256 defendantId
        ) = dispute.executeClaimRuling();
        IOrder OrderContract = IOrder(fetchContract(RegistryKeys.ORDER));
        OrderContract.rule(ruling, orderId, procecutorId, defendantId);
    }

    function nextRound(
        uint256 disputeId,
        DataTypes.Evidence memory evidence
    ) external {
        address caller = _msgSender();
        DataTypes.Dispute storage dispute = DisputeLogic.getDisputeById(
            disputeId,
            _disputes
        );
        DataTypes.DelayTimestamp memory delays = IProtocolConfigurator(
            fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
        ).getDelayTimestamp();
        DataTypes.DisputeParams memory disputeParams = IProtocolConfigurator(
            fetchContract(RegistryKeys.PROTOCOL_CONFIGURATOR)
        ).getDisputeParams();
        uint256 roundNumber = dispute.rounds.length + 1;
        dispute.executeNextRound(
            InputTypes.ExecuteNextRoundInput({
                evidenceUntil: delays.evidence,
                maxVotes: disputeParams.maxVotes * roundNumber,
                totalFeesForJurors: _payDisputeContract(
                    disputeParams.feePerJuror *
                        disputeParams.maxVotes *
                        roundNumber,
                    caller
                ),
                tokensAtStakePerJuror: JuryLogic.calcTokenToFreeze(
                    disputeParams.minStake,
                    disputeParams.alpha
                ),
                drawnJurors: drawJurors(disputeParams.maxVotes * roundNumber)
            })
        );
        sendEvidence(disputeId, caller, evidence);
    }

    function _handleCorrectVoter(
        address voter,
        uint256 tokensAtStakePerJuror,
        uint256 amountFromDisputeFees,
        uint256 amountFromJurorsTokensAtStake
    ) internal {
        IJury JuryContract = IJury(fetchContract(RegistryKeys.JURY));
        IBSWAN BSWANContract = IBSWAN(fetchContract(RegistryKeys.DAT));
        JuryContract.unfreezeTokens(tokensAtStakePerJuror, voter);
        _transfer(
            amountFromDisputeFees,
            address(JuryContract),
            address(BSWANContract)
        );
        JuryContract.rewardJuror(
            amountFromDisputeFees + amountFromJurorsTokensAtStake,
            voter
        );
    }

    function _handleInRangeVoter(
        address voter,
        uint256 tokensAtStakePerJuror
    ) internal {
        IJury JuryContract = IJury(fetchContract(RegistryKeys.JURY));
        JuryContract.unfreezeTokens(tokensAtStakePerJuror, voter);
    }

    function _safeTransferFrom(
        uint256 value,
        address from,
        address to,
        address currency
    ) internal {
        IERC20(currency).safeTransferFrom(from, to, value);
    }

    function _transfer(uint256 amount, address _to, address currency) internal {
        IERC20(currency).safeTransfer(_to, amount);
    }

    function _payDisputeContract(
        uint256 amount,
        address payer
    ) internal returns (uint256 amountBought) {
        IBSWAN BSWANContract = IBSWAN(fetchContract(RegistryKeys.DAT));
        _safeTransferFrom(
            amount,
            payer,
            address(this),
            address(BSWANContract.currency())
        );
        amountBought = BSWANContract.estimateBuyValue(amount);
        BSWANContract.buy(address(this), amount, amountBought);
    }

    function isCallerUser(
        address caller,
        uint256 userId,
        IUser UserContract
    ) public view returns (bool isAddressMatchingId) {
        isAddressMatchingId = UserContract.getIdByAddress(caller) == userId;
    }

    function getOrderPrice(
        uint256 orderId
    ) public view returns (uint256 price) {
        price = IOrder(fetchContract(RegistryKeys.ORDER))
            .getOrderById(orderId)
            .invoice
            .price;
    }
}

